import React, { useState, useEffect, Fragment } from 'react';
import LineItem from './LineItem';
import CreateLineItem from './CreateLineItem';
import '../App.css'

class EditInvoiceModal extends React.Component {
  // const [name, setName] = useState(invoice.name);
  // const [email, setEmail] = useState(invoice.email);
  // const [dueDate, setDueDate] = useState(invoice.due_date.split('T')[0]);
  // const [total, setTotal] = useState(invoice.total);
  // const [lineItems, setLineItems] = useState([])
  constructor(props) {
    super(props);
    this.state = {
      name: props.invoice.name,
      email: props.invoice.email,
      dueDate: props.invoice.due_date.split('T')[0],
      total: props.invoice.total,
      lineItems: [],
    }
  }

  // delete invoice function
  handleInvoiceDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:8080/invoices/${id}`, {
        method: 'DELETE'
      })
      console.log(response);
      window.location = '/';
    } catch (err) {
      console.error(err.message);
    }
  }

  // edit invoice function
  handleInvoiceEdit = async (e) => {
    e.preventDefault();
    try {
      const body = { name , email, dueDate, total} 
      await fetch(`http://localhost:8080/invoices/${invoice.invoice_id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body)
      });
      // refresh page and reload invoices
      window.location = '/';
    } catch (err) {
      console.error(err.message); 
    }  
  }

  // async function to fetch all line item data matching email
  getLineItems = async () => {
    try {
      const response = await fetch(`http://localhost:8080/lineitems/${invoice.email}`);
      const data = await response.json();
      this.setState({lineItems: data})
    } catch (err) {
      console.error(err.message);
    }
  };

  // invokes getLineItems when component mounts/updates
  // useEffect(() => {
  //   getLineItems(invoice.email)
  // },[]); 
  // updates state property according to event target name
  handleChange = (e) => {
    const key = e.target.name;
    this.setState({ [key]: e.target.value })
  };

  componentDidUpdate() {
    this.getLineItems(props.ivnoice.email)
  }

  render() {
    return(
      <Fragment>
        <button type="button" className="btn btn-primary" data-toggle="modal" data-target={`#id${props.invoice.invoice_id}`}>
        Edit
        </button>
          <div className="modal" id={`id${props.invoice.invoice_id}`}>
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h4 className="modal-title">Invoice App</h4>
                  <button type="button" className="close" data-dismiss="modal">&times;</button>
                </div>
                <div className="modal-body">
                  <div className="modal-container">
                    <form className="invoice-form">
                      <div className="input-wrapper">
                        <label className="input-label">Name</label>
                        <input 
                          type="text" 
                          className="input" 
                          value={name}
                          onChange={this.handleChange}
                        ></input>
                      </div>
                      <div className="input-wrapper">
                        <label className="input-label">Email</label>
                        <input 
                          type="text" 
                          className="input"
                          value={email}
                          onChange={this.handleChange}
                        ></input>
                      </div>
                      <div className="input-wrapper">
                        <label className="input-label">Due Date</label>
                        <input 
                          type="date" 
                          className="input"
                          value={dueDate} 
                          onChange={(e) => setDueDate(e.target.value)}
                        ></input>
                      </div>
                    </form>
                    <CreateLineItem email={email}/>
                      {lineItems.map((line, index) => {
                        return <LineItem description={line.description} amount={line.amount} key={index} />
                      })}
                    <div className="totals-wrapper">
                      <label>TOTAL</label>
                      <p>{props.invoice.total}</p>
                    </div>
                  </div>
                </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" data-dismiss="modal">BACK</button>
                <button type="submit" className="btn btn-danger" data-dismiss="modal" onClick={() => this.handleInvoiceDelete(props.invoice.invoice_id)}>DELETE</button>
                <button type="submit" className="btn btn-success" data-dismiss="modal" onClick={(e) => this.handleInvoiceEdit(e)}>SAVE</button>
              </div>
            </div>
          </div>
        </div>
      </Fragment>
    )
  }
}

export default EditInvoiceModal;
