import React from 'react';
import '../App.css'

const LineItem = (props) => {

  return (

    <div 
    className="line-item-display"
    style={{display: 'flex', }}
    >
      <p>{props.description}</p>
      <p>{props.amount}</p>
      <hr/>
    </div>
  )
}

export default LineItem;
