import React from 'react';
import { Link } from 'react-router-dom';
import LineItemList from './LineItemList';
import '../App.css'

class CreateInvoice extends React.Component {

  // const [name, setName] = useState("")
  // const [email, setEmail] = useState("")
  // const [dueDate, setDueDate] = useState("")
  // const [total, setTotal] = useState("")

  // useEffect(() => {
  //   setTotal(0.00)
  // }, []); 

  constructor() {
    super();
    this.state = {
      name: '',
      email: '',
      dueDate: '',
      total: 0.00,
      lineItems: [],
    }
  }

  handleInvoiceSave = async (e) => {
    e.preventDefault();
    const { name, email, dueDate, total } = this.state
    const body = { name , email, dueDate, total}
    try {
      await fetch('http://localhost:8080/invoices', {
        method: 'POST',
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify(body)
      });
      // redirect to home page
      window.location = '/'
    } catch (err) {
      console.error(err.message); 
    }  
  };

  handleChange = (e) => {
    const key = e.target.name;
    this.setState({ [key] : e.target.value})
  }

  render() {
    return(
      <div className="invoice-container">
        <form className="invoice-form">
          <div className="input-wrapper">
            <label className="input-label">Name</label>
            <input 
              type="text" 
              name="name" 
              className="input" 
              onChange={(e) => this.name = e.target.value}
            ></input>
          </div>
          <div className="input-wrapper">
            <label className="input-label">Email</label>
            <input 
              type="text" 
              name="email" 
              className="input"
              onChange={(e) => this.state.email = e.target.value}
            ></input>
          </div>
          <div className="input-wrapper">
            <label className="input-label">Due Date</label>
            <input type="date" name="dueDate" onChange={(e) => this.state.dueDate = e.target.value}></input>
          </div>
        </form>
        <LineItemList />
        <div className="totals-wrapper">
          <label>TOTAL</label>
          <p>{this.state.total}</p>
        </div>
        <div className="buttonContainer">
          <Link to="/"><button type="submit">BACK</button></Link>
          <button type="submit" onClick={this.handleInvoiceSave}>CREATE</button>
        </div>
      </div>
    )
  }
}

export default CreateInvoice;
