import React, { useState, useEffect, Fragment } from 'react';
// import LineItemList from './LineItemList'
import '../App.css'

const EditInvoiceModal = ({ invoice }) => {
  const [name, setName] = useState(invoice.name)
  const [email, setEmail] = useState(invoice.email)
  const [dueDate, setDueDate] = useState(invoice.due_date)
  const [total, setTotal] = useState(invoice.total)

  // const editInvoice = async (id) => {
  //   try {
  //     const editData = await fetch(`http://localhost:8080/invoices/${id}`, {
  //       method: 'PUT'
  //     });
  //     const data = await editData.json();

  //     // setInvoices(data);
  //   } catch (err) {
  //     console.error(err.message);
  //   }
  // };

  const handleInvoiceDelete = async (id) => {
    try {
      await fetch(`http://localhost:8080/invoices/${id}`, {
        method: 'DELETE'
      })
      .then()
      //console.log(deleteData);
    } catch (err) {
      console.error(err.message);
    }
  }

  useEffect(() => {
    setTotal(0.00)
  }, []); 

  const handleInvoiceEdit = async (e) => {
    e.preventDefault();
    const body = { name , email, dueDate, total}
    console.log(body)
    try {
      const response = await fetch('http://localhost:8080/invoices', {
        method: 'POST',
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify(body)
      })
      console.log(body, response)
    } catch (err) {
      console.error(err.message); 
    }  
  }

  return(
    <Fragment>
      <button type="button" className="btn btn-primary" data-toggle="modal" data-target={`#id${invoice.invoice_id}`}>
       Edit
      </button>
        <div className="modal" id={`id${invoice.invoice_id}`}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h4 className="modal-title">Invoice App</h4>
                <button type="button" className="close" data-dismiss="modal">&times;</button>
              </div>
              <div className="modal-body">
                <div className="invoice-container">
                  <form className="invoice-form">
                    <div className="input-wrapper">
                      <label className="input-label">Name</label>
                      <input 
                        type="text" 
                        className="input" 
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                      ></input>
                    </div>
                    <div className="input-wrapper">
                      <label className="input-label">Email</label>
                      <input 
                        type="text" 
                        className="input"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                      ></input>
                    </div>
                    <div className="input-wrapper">
                      <label className="input-label">Due Date</label>
                      <input 
                        type="date" 
                        value={dueDate} 
                        onChange={(e) => setDueDate(e.target.value)}
                      ></input>
                    </div>
                  </form>
                  {/* <LineItemList /> */}
                  <div className="totals-wrapper">
                    <label>TOTAL</label>
                    <p onClick={(() => console.log(invoice.name))}>{total}</p>
                  </div>
                </div>
              </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-dismiss="modal">BACK</button>
              <button type="submit" className="btn btn-danger" data-dismiss="modal" onClick={() => handleInvoiceDelete(invoice.invoice_id)}>DELETE</button>
              <button type="submit" className="btn btn-success" onClick={(e) => handleInvoiceEdit(e)}>SAVE</button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  )
}

export default EditInvoiceModal;
